      'gelatoLab.hero.stars': 'â­ Stelle: 8/8',
      'gelatoLab.hero.title': 'Gelato Lab',
      'gelatoLab.hero.desc': 'Manuale per il banco gelato: porzioni, servizio take me home, coppe scenografiche e manutenzione della vetrina a -14/-15 Â°C.',
      'gelatoLab.carousel.products.category': 'Linea gelato',
